import React, { useEffect, useState } from "react";
import { useGameInfo } from "../../context/GameContext";
import "./Board.css";
import Card from "../CardComponent/Card";
import TextField from "../TextFieldComponent/TextField";
import variableService from "../../services/variableService";
import ButtonField from "../ButtonFieldComponent/ButtonField";

export default function Header({ initGame }) {
  const {
    cards,
    setCards,
    couplesFound,
    setCouplesFound,
    choiceOne,
    setChoiceOne,
    choiceTwo,
    setChoiceTwo,
    turns,
    setTurns,
  } = useGameInfo();

  const [win, setWin] = useState(false);

  //check if you win when a couple is found
  useEffect(() => {
    setWin(couplesFound === variableService.getCardImages().length);
  }, [couplesFound]);

  return (
    <>
      <TextField text={"MEMORY"} tag={variableService.getTextType().TITLE} />
      {!win && (
        <div className="info">
          <TextField
            text={"#Turn: " + turns}
            tag={variableService.getTextType().PARAGRAPH}
          />
          <TextField
            text={
              "#Couples found: " +
              couplesFound +
              "/" +
              variableService.getCardImages().length
            }
            tag={variableService.getTextType().PARAGRAPH}
          />
        </div>
      )}
      {win && (
        <TextField
          text={"YOU WIN"}
          tag={variableService.getTextType().SUBTITLE}
        />
      )}

      <ButtonField title={win ? "Play again" : "New Game"} onClick={initGame} />
    </>
  );
}
