import { cardImages, CARD_IMAGES, VARIABLES } from "../config/variables";

class VariableService {
  getNumCards() {
    return 2 * VARIABLES.NUM_COPPIE;
  }

  getCardImages() {
    return CARD_IMAGES;
  }
}

export default new VariableService();
