import { createContext, useEffect, useState } from "react";
import "./App.css";
import Board from "./components/BoardCompoent/Board";
import ButtonField from "./components/ButtonFieldComponent/ButtonField";
import PageContent from "./components/PageContentComponent/PageContent";
import TextField from "./components/TextFieldComponent/TextField";
import { TEXT_TYPE } from "./config/variables";
import { useGameInfo } from "./context/GameContext";
import gameService from "./services/gameService";
import variableService from "./services/variableService";

export const GameContext = createContext();

function App() {
  const {
    cards,
    setCards,
    turns,
    setTurns,
    couplesFound,
    setCouplesFound,
    setChoiceOne,
    setChoiceTwo,
  } = useGameInfo();

  const [win, setWin] = useState(false);

  useEffect(() => {
    setWin(couplesFound === VARIABLES.MAX_NUM_COPPIE);
  }, [couplesFound]);

  const initGame = () => {
    setCards(
      gameService.duplicateAndShuffleArray(variableService.getCardImages())
    );
    setCouplesFound(0);
    setTurns(0);
    setChoiceOne(null);
    setChoiceTwo(null);
  };

  const nextTurn = () => {
    setTurns((prevTurns) => prevTurns + 1);
  };

  useEffect(() => {
    initGame();
  }, []);

  return (
    <PageContent>
      <TextField text={"MEMORY"} tag={TEXT_TYPE.TITLE} />

      {!win && (
        <div className="info">
          <TextField text={"#Turn: " + turns} tag={TEXT_TYPE.PARAGRAPH} />
          <TextField
            text={
              "#Couples found: " +
              couplesFound +
              "/" +
              variableService.getCardImages().length()
            }
            tag={TEXT_TYPE.PARAGRAPH}
          />
        </div>
      )}
      {win && <TextField text={"YOU WIN"} tag={TEXT_TYPE.SUBTITLE} />}

      <ButtonField title={win ? "Play again" : "New Game"} onClick={initGame} />
      <Board nextTurn={nextTurn} />
    </PageContent>
  );
}

export default App;
