import { useEffect, useState } from "react";
import "./App.css";
import Board from "./components/BoardCompoent/Board";
import ButtonField from "./components/ButtonFieldComponent/ButtonField";
import PageContent from "./components/PageContentComponent/PageContent";
import TextField from "./components/TextFieldComponent/TextField";
import { TEXT_TYPE } from "./config/variables";
import gameService from "./services/gameService";

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);
  const [couplesFound, setCouplesFound] = useState(0);
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiceTwo, setChoiceTwo] = useState(null);

  const initGame = () => {
    setCards(gameService.shuffleCards());
    setTurns(0);
    setChoiceOne(null);
    setChoiceTwo(null);
  };

  const nextTurn = () => {
    setTurns((prevTurns) => prevTurns + 1);
  };

  useEffect(() => {
    initGame();
  }, []);

  useEffect(() => {
    console.log("CARDS: ", cards);
  }, [cards]);

  return (
    <PageContent>
      <TextField text={"MEMORY"} tag={TEXT_TYPE.TITLE} />
      <div className="info">
        <TextField text={"#Turn: " + turns} tag={TEXT_TYPE.PARAGRAPH} />
        <TextField
          text={"#Couple found: " + couplesFound}
          tag={TEXT_TYPE.PARAGRAPH}
        />
      </div>

      <ButtonField title={"New Game"} onClick={initGame} />
      <Board
        cards={cards}
        setCards={setCards}
        choiceOne={choiceOne}
        setChoiceOne={setChoiceOne}
        choiceTwo={choiceTwo}
        setChoiceTwo={setChoiceTwo}
        coupleFound={couplesFound}
        setCouplesFound={setCouplesFound}
        nextTurn={nextTurn}
      />
    </PageContent>
  );
}

export default App;
