import parse from "html-react-parser";

class ParseService {
  parseHtml(html, tag) {
    return parse(html, {
      replace: (domNode) => {
        if (domNode.type !== "text" && tag) {
          return <></>;
        }
      },
    });
  }
}

export default new ParseService();
