import React, { useEffect, useState } from "react";
import { useGameInfo } from "../../context/GameContext";
import "./Board.css";
import Card from "../CardComponent/Card";
import TextField from "../TextFieldComponent/TextField";
import variableService from "../../services/variableService";

export default function Header() {
  const {
    cards,
    setCards,
    couplesFound,
    setCouplesFound,
    choiceOne,
    setChoiceOne,
    choiceTwo,
    setChoiceTwo,
    turns,
    setTurns,
  } = useGameInfo();

  return (
    <>
      <TextField text={"MEMORY"} tag={variableService.getTextType().TITLE} />
      {!win && (
        <div className="info">
          <TextField
            text={"#Turn: " + turns}
            tag={variableService.getTextType().PARAGRAPH}
          />
          <TextField
            text={
              "#Couples found: " +
              couplesFound +
              "/" +
              variableService.getCardImages().length
            }
            tag={variableService.getTextType().PARAGRAPH}
          />
        </div>
      )}
      {win && (
        <TextField
          text={"YOU WIN"}
          tag={variableService.getTextType().SUBTITLE}
        />
      )}

      <ButtonField title={win ? "Play again" : "New Game"} onClick={initGame} />
    </>
  );
}
