import { createContext, useEffect, useState } from "react";
import "./App.css";
import Board from "./components/BoardCompoent/Board";
import ButtonField from "./components/ButtonFieldComponent/ButtonField";
import PageContent from "./components/PageContentComponent/PageContent";
import TextField from "./components/TextFieldComponent/TextField";
import { TEXT_TYPE, VARIABLES } from "./config/variables";
import { useGameInfo } from "./context/GameContext";
import gameService from "./services/gameService";

export const GameContext = createContext();

function App() {
  const {
    cards,
    setCards,
    turns,
    setTurns,
    couplesFound,
    setCouplesFound,
    setChoiceOne,
    setChoiceTwo,
  } = useGameInfo();

  const [win, setWin] = useState(false);

  useEffect(() => {
    setWin(couplesFound === VARIABLES.NUM_COPPIE);
    if (couplesFound === VARIABLES.NUM_COPPIE) {
      alert("You win");
    }
  }, [couplesFound]);

  const initGame = () => {
    setCards(gameService.shuffleCards());
    setCouplesFound(0);
    setTurns(0);
    setChoiceOne(null);
    setChoiceTwo(null);
  };

  const nextTurn = () => {
    setTurns((prevTurns) => prevTurns + 1);
  };

  useEffect(() => {
    initGame();
  }, []);

  useEffect(() => {
    console.log("CARDS: ", cards);
  }, [cards]);

  return (
    <PageContent>
      <TextField text={"MEMORY"} tag={TEXT_TYPE.TITLE} />

      {!win && (
        <div className="info">
          <TextField text={"#Turn: " + turns} tag={TEXT_TYPE.PARAGRAPH} />
          <TextField
            text={"#Couple found: " + couplesFound + "/" + VARIABLES.NUM_COPPIE}
            tag={TEXT_TYPE.PARAGRAPH}
          />
        </div>
      )}

      <ButtonField title={"New Game"} onClick={initGame} />
      <Board nextTurn={nextTurn} />
    </PageContent>
  );
}

export default App;
