import React, { useEffect, useState } from "react";
import gameService from "../../services/gameService";

import Card from "../CardComponent/Card";
import "./Board.css";

export default function Board({ nextTurn }) {
  const [disabled, setDisabled] = useState(false);

  const handleChoice = (card) => {
    console.log(card);
    gameService.getChoiceOne
      ? gameService.setChoiceTwo(card)
      : gameService.setChoiceOne(card);
  };

  const resetTurns = () => {
    gameService.setChoiceOne(null);
    gameService.setChoiceTwo(null);
    nextTurn();
    setDisabled(false);
  };

  useEffect(() => {
    if (gameService.getChoiceOne() && gameService.getChoiceTwo()) {
      setDisabled(true);
      if (gameService.getChoiceOne().src === gameService.getChoiceTwo().src) {
        console.log("IT'S A MATCH");
        gameService.setCards((prevCards) => {
          return prevCards.map((card) => {
            if (card.src === gameService.getChoiceOne().src) {
              return { ...card, matched: true };
            } else {
              return card;
            }
          });
        });
        resetTurns();
      } else {
        console.log("IT'S NOT A MATCH");
        setTimeout(() => resetTurns(), 1000);
      }
    }
  }, [gameService.getChoiceOne(), gameService.getChoiceTwo()]);

  return (
    <div className="board-grid">
      {gameService.getCards().map((card) => (
        <Card
          key={card.id}
          card={card}
          handleChoice={handleChoice}
          flipped={
            card === gameService.getChoiceOne() ||
            card === gameService.getChoiceTwo() ||
            card.matched
          }
          disabled={disabled}
        ></Card>
      ))}
    </div>
  );
}
