import { createContext, useEffect, useState } from "react";
import "./App.css";
import Board from "./components/BoardCompoent/Board";
import Header from "./components/HeaderComponent/Header";
import PageContent from "./components/PageContentComponent/PageContent";
import StartMenu from "./components/StartMenuComponent/StartMenu";
import { useGameInfo } from "./context/GameContext";
import gameService from "./services/gameService";
import variableService from "./services/variableService";

export const GameContext = createContext();

function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const { setDeck, setTurns, setCouplesFound, setFirstCard, setSecondCard } =
    useGameInfo();

  //initialize game
  const initGame = () => {
    setDeck(gameService.duplicateAndShuffleArray(variableService.getCards()));
    setCouplesFound(0);
    setTurns(0);
    setFirstCard(null);
    setSecondCard(null);
  };

  const handleOnClick = (val) => {
    initGame();
    setGameStarted(true);
  };

  useEffect(() => {
    initGame();
  }, []);

  return (
    <PageContent>
      {!gameStarted && <StartMenu onStartGame={setGameStarted} />}
      {gameStarted && (
        <>
          <Header onRestartGame={setGameStarted} />
          <Board onEndGame={setGameStarted} />
        </>
      )}
    </PageContent>
  );
}

export default App;
