import React, { useEffect, useState } from "react";
import { TEXT_TYPE } from "../../config/variables";

export default function TitleField({ text, tag }) {
  const [componentType, setComponentType] = useState();

  useEffect(() => {
    setComponentType(tag);
  }, [tag]);

  switch (componentType) {
    case TEXT_TYPE.TITLE:
      return <h1>{text}</h1>;
    case TEXT_TYPE.SUBTITLE:
      return <h4>{text}</h4>;

    case TEXT_TYPE.PARAGRAPH:
      return <p>{text}</p>;

    default:
      return <p>{text}</p>;
  }

  return (
    <div className="title">
      <h1>{text}</h1>
    </div>
  );
}
