import variableService from "./variableService";

class GameService {
  #cards = [];
  #turn = 0;
  #choiceOne = null;
  #choiceTwo = null;

  getCards() {
    return this.#cards;
  }
  setCards(cards) {
    this.#cards = cards;
  }

  getTurn() {
    return this.#cards;
  }
  setTurn(turn) {
    this.#cards = cards;
  }

  getChoiceOne() {
    return this.#choiceOne;
  }
  setChoiceOne(choice) {
    this.#choiceOne = choice;
  }
  getChoiceTwo() {
    return this.#choiceTwo;
  }
  setChoiceTwo(choice) {
    this.#choiceTwo = choice;
  }

  shuffleCards() {
    const shuffleCards = [
      ...variableService.getCardImages(),
      ...variableService.getCardImages(),
    ]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({ ...card, id: Math.random() }));
    return shuffleCards;
  }
}

export default new GameService();
