class GameService {
  duplicateAndShuffleArray(myArray) {
    const shuffleCards = [...myArray, ...myArray]
      .sort((a, b) => 0.5 - Math.random())
      .map((element) => ({ ...element, id: element.id, key: Math.random() }));
    return shuffleCards;
  }
}

export default new GameService();
