import { useEffect, useState } from "react";
import "./App.css";
import Board from "./components/BoardCompoent/Board";
import ButtonField from "./components/ButtonFieldComponent/ButtonField";
import PageContent from "./components/PageContentComponent/PageContent";
import TitleField from "./components/TitleFieldComponent/TitleField";
import gameService from "./services/gameService";

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);

  const initGame = () => {
    gameService.setCards(gameService.shuffleCards());
    setCards();
    setTurns(0);
  };

  const nextTurn = () => {
    setTurns((prevTurns) => prevTurns + 1);
  };

  useEffect(() => {
    initGame();
  }, []);

  useEffect(() => {
    console.log(cards);
  }, [cards]);

  return (
    <PageContent>
      <TitleField title={"MEMORY"} />
      <TitleField title={turns} />

      <ButtonField title={"New Game"} onClick={initGame} />
      <Board cards={cards} nextTurn={nextTurn} setCards={setCards} />
    </PageContent>
  );
}

export default App;
