import React, { useEffect, useState } from "react";
import { useGameInfo } from "../../context/GameContext";
import TextField from "../TextFieldComponent/TextField";
import variableService from "../../services/variableService";
import ButtonField from "../ButtonFieldComponent/ButtonField";

export default function Header({ onRestartGame }) {
  const { couplesFound, turns } = useGameInfo();

  const [win, setWin] = useState(false);

  //check if you win when a couple is found
  useEffect(() => {
    setWin(couplesFound === variableService.getCards().length);
  }, [couplesFound]);

  return (
    <div>
      <TextField
        text={variableService.getLabels().GAME_TITLE.toUpperCase()}
        tag={variableService.getTextType().TITLE}
      />
      {!win && (
        <div className="flex justify-space-evenly">
          <TextField
            text={variableService.getLabels().NUM_TURN + ": " + turns}
            tag={variableService.getTextType().PARAGRAPH}
          />
          <TextField
            text={
              variableService.getLabels().NUM_COUPLES +
              ": " +
              couplesFound +
              "/" +
              variableService.getCards().length
            }
            tag={variableService.getTextType().PARAGRAPH}
          />
        </div>
      )}
      {win && (
        <TextField
          text={variableService.getLabels().WIN_TEXT.toUpperCase()}
          tag={variableService.getTextType().SUBTITLE}
        />
      )}

      <ButtonField
        className={"absolute top-1 left-1"}
        title={"BACK"}
        onClick={() => onRestartGame(false)}
      />
    </div>
  );
}
