import variableService from "./variableService";

class GameService {
  shuffleCards() {
    const shuffleCards = [
      ...variableService.getCardImages(),
      ...variableService.getCardImages(),
    ]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({ ...card, id: card.id }));
    return shuffleCards;
  }
}

export default new GameService();
