import { createContext, useEffect, useState } from "react";
import "./App.css";
import Board from "./components/BoardCompoent/Board";
import ButtonField from "./components/ButtonFieldComponent/ButtonField";
import PageContent from "./components/PageContentComponent/PageContent";
import TextField from "./components/TextFieldComponent/TextField";
import { useGameInfo } from "./context/GameContext";
import gameService from "./services/gameService";
import variableService from "./services/variableService";

export const GameContext = createContext();

function App() {
  const {
    setCards,
    turns,
    setTurns,
    couplesFound,
    setCouplesFound,
    setChoiceOne,
    setChoiceTwo,
  } = useGameInfo();

  const [win, setWin] = useState(false);

  //check if you win when a couple is found
  useEffect(() => {
    setWin(couplesFound === variableService.getCardImages().length);
  }, [couplesFound]);

  //initialize game
  const initGame = () => {
    setCards(
      gameService.duplicateAndShuffleArray(variableService.getCardImages())
    );
    setCouplesFound(0);
    setTurns(0);
    setChoiceOne(null);
    setChoiceTwo(null);
  };

  useEffect(() => {
    initGame();
  }, []);

  return (
    <PageContent>
      <Board />
    </PageContent>
  );
}

export default App;
